import React, { useState, useEffect } from "react";
import axios from "axios";
//import Table from "../layouts/Table";

const Documents = (props) => {
  /*   const tableTitle = "Services";
  const entity = "services";
  const addButton = "Add Service";
  const headerItems = [
    "Service#",
    "Vehicle#",
    "Registration",
    "Service Type",
    "Start Date",
    "Completion Date",
    "Repairer",
    "Action",
  ];
  const columnOrder = [
    "serviceNum",
    "vehicleNum",
    "regNum",
    "serviceType",
    "startDate",
    "completionDate",
    "repairer",
  ];
 */
  const parentDbId = props.parentDbId;
  //console.log(parentDbId);

  const [documents, setDocuments] = useState([]);

  useEffect(() => {
    //  console.log(parentDbId);
    loadDocuments();
  }, []);

  useEffect(() => {
    //  console.log(parentDbId);
    loadDocuments();
  }, [documents]);

  const loadDocuments = async () => {
    //  console.log(parentDbId);
    // const parentDbId = "1";
    const result = await axios.get(
      `http://localhost:5000/documents/${parentDbId}`
    );
    setDocuments(result.data);
  };

  // loadDocuments(parentDbId);

  /*   const deleteDocument = async (id) => {
    await axios.delete(`http://localhost:5000/documents/${id}`);
    loadDocuments();
  }; */

  return (
    <>
      {documents.map((document, index) => (
        <a href={document.filePath} key={index}>
          {document.fileName}
        </a>
      ))}
      {/*       <Table
        tableTitle={tableTitle}
        entity={entity}
        headerItems={headerItems}
        tableData={documents}
        columnOrder={columnOrder}
        deleteRecord={deleteDocument}
        addButton={addButton}
      /> */}
    </>
  );
};

export default Documents;
