import React, { useState, useEffect } from "react";
import axios from "axios";
import BsTable from "../layouts/BsTable";
import { textFilter } from "react-bootstrap-table2-filter";
import UserAuth from "../auth/UserAuth";

const OdoReadings = () => {
  const drilldown = (odoNum, row) => {
    return (
      <>
        <Link
          //className="btn btn-primary btn-sm me-3"
          to={`odoReadings/${id}`}
        >
          odoNum
          {/* View */}
        </Link>
        <Link
          // className="btn btn-warning btn-sm me-3"
          to={`odoReadings/edit/${id}`}
        >
          odoNum
          {/* Edit */}
        </Link>
      </>
    );
  };
  const columns = [
    {
      dataField: "odoNum",
      text: "Odometer#",
      sort: true,
      sortFunc: (a, b, order, dataField) => {
        if (order === "asc" || !order) {
          return b.localeCompare(
            a,
            navigator.languages[0] || navigator.language,
            { numeric: true, ignorePunctuation: true }
          );
        }
        return a.localeCompare(
          b,
          navigator.languages[0] || navigator.language,
          { numeric: true, ignorePunctuation: true }
        );
      },
      formatter: drilldown,
    },
    /*     {
      dataField: "vehicleNum",
      text: "Vehicle#",
    }, */
    {
      dataField: "regNum",
      text: "Registration",
      filter: textFilter(),
    },
    {
      dataField: "readingDate",
      text: "Date",
    },
    {
      dataField: "initialReading",
      text: "Initial Reading",
    },
    {
      dataField: "finalReading",
      text: "Final Reading",
    },
    {
      dataField: "mileage",
      text: "Mileage",
    },
    /*     {
      dataField: "id",
      text: "Actions",
      formatter: action,
    }, */
  ];
  //for bootstrap table

  const [odoReadings, setOdoReadings] = useState([]);

  useEffect(() => {
    loadOdoReadings();
  }, []);

  const loadOdoReadings = async () => {
    const result = await axios.get("http://localhost:5000/odoReadings");
    // console.log(result);
    //  result.data.map((item) => delete item.id);
    setOdoReadings(result.data);
  };

  // delete odo reading
  /*   const deleteOdoReading = async (id) => {
    await axios.delete(`http://localhost:5000/odoReadings/${id}`);
    loadOdometer();
  }; */

  const defaultSorted = [
    {
      dataField: "odoNum", // if dataField is not match to any column you defined, it will be ignored.
      order: "asc", // desc or asc
    },
  ];

  return (
    <UserAuth>
      <BsTable
        sObject="OdoReadings"
        tableTitle="Odometer"
        columns={columns}
        tableData={odoReadings}
        addButton="Add Reading"
        defaultSorted={defaultSorted}
      />
    </UserAuth>
  );
};

export default OdoReadings;
