import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Modal, Button } from "react-bootstrap";
import ConfirmationModal from "../layouts/ConfirmationModal";

const Table = (props) => {
  const [modal, setModal] = useState({ showModal: false, id: "" });

  const handleModal = (showModal, RecordId) => {
    //  setModal(true);
    // setModal({ showModal: true, id: RecordId });
    //  setVehicle({ ...vehicle, [event.target.name]: event.target.value });
    setModal({ ...modal, [showModal]: true });
  };

  const handleCloseModal = () => {
    setModal({ showModal: false, id: "" });
  };

  return (
    <>
      <div className="table-responsive">
        <div className="table-wrapper">
          <div className="table-title bg-primary">
            <div className="row">
              <div className="col-sm-6">
                <h2>
                  Manage <b>{props.tableTitle}</b>
                </h2>
              </div>
              <div className="col-sm-6">
                <Link className="btn btn-dark" to="vehicles/add">
                  Add Vehicle
                </Link>
              </div>
            </div>
          </div>

          <table className="table table-striped table-hover">
            <thead>
              <tr>
                <th scope="col">#</th>
                {props.headerItems.map((headerItem, index) => (
                  <th key={index} scope="col">
                    {headerItem}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.values(props.tableData).map((rowData, index) => (
                <tr>
                  <th key={index} scope="row">
                    {index + 1}
                  </th>

                  {props.orderedColumns.map((orderedColumn, index2) => (
                    <td key={index2}>{rowData[orderedColumn]}</td>
                  ))}

                  {/*                   {Object.values(rowData).map((colValue, index2) => (
                    <td key={index2}>{colValue}</td>
                  ))} */}

                  <td>
                    <Link
                      className="btn btn-primary me-2"
                      to={`${props.entity}/${rowData.id}`}
                    >
                      View
                    </Link>
                    <Link
                      className="btn btn-warning me-2"
                      to={`${props.entity}/${rowData.id}`}
                    >
                      Edit
                    </Link>
                    <Link
                      className="btn btn-danger"
                      // onClick={() => props.deleteRecord(rowData.id)}
                      onClick={handleModal(true, rowData.id)}
                      to={`/${props.entity}`}
                    >
                      Delete
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {/*       <Modal show={show} onHide={handleClose}>
        <Modal.Header>
          <Modal.Title>Add Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete ?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close Button
          </Button>
        </Modal.Footer>
      </Modal> */}
      <ConfirmationModal
        show={modal.showModal}
        id={modal.id}
        handleCloseModal={handleCloseModal}
        handleDelete={props.deleteRecord}
      />
    </>
  );
};

export default Table;
