import React from "react";
import { Link } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import cellEditFactory, { Type } from "react-bootstrap-table2-editor";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";
import { BsFillPencilFill } from "react-icons/bs";

const BsTable = (props) => {
  const action = (id, row) => {
    return (
      <>
        <Link className="btn btn-primary me-2" to={`${props.sObject}/${id}`}>
          View
        </Link>
        <Link to={`${props.sObject}/edit/${id}`}>
          <BsFillPencilFill size="2px" />
        </Link>
      </>
    );
  };
  const columns = [
    ...props.columns,
    {
      dataField: "id",
      text: "Actions",
      formatter: action,
    },
  ];

  //const rowStyle = { height: "15px", padding: "5px 0" };

  return (
    <div className="table-container">
      <div className="container table-container">
        <div className="card shadow">
          <div className="card-body">
            <h2 className="card-title d-inline-block table-label">
              Manage {props.tableTitle}
            </h2>
            {/*  <img src="../node_modules/bootstrap-icons/icons/alarm.svg" alt="" /> */}
            <Link
              className="btn btn-primary px-2 py-2 mt-1 ml-3 d-inline-block"
              to={`${props.sObject}/add`}
            >
              {props.addButton}
            </Link>
            <BootstrapTable
              keyField="id"
              data={props.tableData}
              columns={columns}
              striped
              hover
              bordered={false}
              condensed
              //  rowStyle={rowStyle}
              pagination={paginationFactory()}
              cellEdit={cellEditFactory({
                mode: "dbclick",
              })}
              filter={filterFactory()}
            />
          </div>
        </div>
      </div>
    </div>
  );
};
export default BsTable;
