import React, { useState } from "react";
import Axios from "axios";
import { useHistory } from "react-router-dom";

const AddVehicle = () => {
  let history = useHistory();
  const [vehicle, setVehicle] = useState({
    vehicleNum: "",
    regNum: "",
    vin: "",
    make: "",
    model: "",
    buildDate: "",
  });

  const onInputChange = (event) => {
    //  console.log(event.target.value);
    setVehicle({ ...vehicle, [event.target.name]: event.target.value });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      // console.log(vehicle);
      await Axios.post("http://localhost:5000/vehicles", vehicle);
      history.push("/vehicles");
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div className="container">
      <div className="w-80 mx-auto shadow p-5">
        <h2 className="text-center">Add Vehicle</h2>
        <hr />
        <div class="row g-3">
          <div class="col-md-3 m-5">
            <div class="mb-2">
              <h5 className="text-center">Information</h5>
              <hr />

              <label for="vehicleNum" class="form-label mb-0">
                Vehicle#
              </label>
              <input type="text" class="form-control mt-0" id="vehicleNum" />
            </div>

            <label for="make" class="form-label mb-0">
              Make
            </label>
            <input type="text" class="form-control mt-0" id="make" />
          </div>

          <div class="col-md-3">
            <div class="mb-2">
              <h5 className="text-center">Registration</h5>
              <hr />
              <label for="regNum" class="form-label mb-0">
                Registration#
              </label>
              <input type="text" class="form-control mt-0" id="regNum" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddVehicle;
