import React, { useState, useEffect } from "react";
import axios from "axios";
//import Table from "../layouts/Table";

const Documents = (props) => {
  /*   const tableTitle = "Services";
  const entity = "services";
  const addButton = "Add Service";
  const headerItems = [
    "Service#",
    "Vehicle#",
    "Registration",
    "Service Type",
    "Start Date",
    "Completion Date",
    "Repairer",
    "Action",
  ];
  const columnOrder = [
    "serviceNum",
    "vehicleNum",
    "regNum",
    "serviceType",
    "startDate",
    "completionDate",
    "repairer",
  ];
 */
  const parentDbId = props.parentDbId;
  //console.log(parentDbId);

  const [documents, setDocuments] = useState([]);

  //mount
  useEffect(() => {
    loadDocuments();
    return setDocuments([]);
  }, []);

  //update
  useEffect(() => {
    loadDocuments();
    return setDocuments([]);
  }, [documents]);

  /*   //unmount
  useEffect(() => {
    //  console.log(parentDbId);
    return setDocuments({});
  }, []); */

  const loadDocuments = async () => {
    //  console.log(parentDbId);
    // const parentDbId = "1";
    const result = await axios.get(
      `http://localhost:5000/documents/${parentDbId}`
    );
    setDocuments(result.data);
  };

  // loadDocuments(parentDbId);

  /*   const deleteDocument = async (id) => {
    await axios.delete(`http://localhost:5000/documents/${id}`);
    loadDocuments();
  }; */

  return (
    <>
      <div className="row">
        {documents.map((document, index) => (
          <div className="col-lg-3" key={index}>
            <a href={document.filePath}>{document.fileName}</a>
          </div>
        ))}
      </div>
    </>
  );
};

export default Documents;
