import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
//import Table from "../layouts/Table";

const Documents = (props) => {
  const parentDbId = props.parentDbId;
  //console.log(parentDbId);

  const [documents, setDocuments] = useState([]);
  //const isInitialMount = useRef(true);

  //mount
  useEffect(() => {
    console.log("first use effect");
    loadDocuments();
  }, []);

  //update
  useEffect(() => {
    console.log("second use effect");
    loadDocuments();
  }, [documents.length]);

  //unmount
  /*   useEffect(() => {
    console.log("second use effect");
    return setDocuments([]);
  }, []);
 */
  const loadDocuments = async () => {
    //  console.log(parentDbId);
    // const parentDbId = "1";
    const result = await axios.get(
      `http://localhost:5000/documents/${parentDbId}`
    );
    setDocuments(result.data);
  };

  // loadDocuments(parentDbId);

  /*   const deleteDocument = async (id) => {
    await axios.delete(`http://localhost:5000/documents/${id}`);
    loadDocuments();
  }; */

  return (
    <>
      <div className="row">
        {documents.map((document, index) => (
          <div className="col-lg-3" key={index}>
            <a href={document.filePath}>{document.fileName}</a>
          </div>
        ))}
      </div>
    </>
  );
};

export default Documents;
