import React, { useState } from "react";
import Axios from "axios";
import { useHistory } from "react-router-dom";

const AddVehicle = () => {
  let history = useHistory();
  const [vehicle, setVehicle] = useState({
    vehicleNum: "",
    regNum: "",
    vin: "",
    make: "",
    model: "",
    buildDate: "",
  });

  const onInputChange = (event) => {
    //  console.log(event.target.value);
    setVehicle({ ...vehicle, [event.target.name]: event.target.value });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    try {
      // console.log(vehicle);
      await Axios.post("http://localhost:5000/vehicles", vehicle);
      history.push("/vehicles");
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div className="container m-5">
      <div className="shadow p-2">
        <h2 className="text-center">Add Vehicle</h2>
        <hr />
        <div class="row g-3">
          <div class="col-md-3 m-3">
            <h5 className="text-center">Information</h5>
            <hr />
            <div class="mb-3">
              <label for="vehicleNum" class="form-label mb-0">
                Vehicle#
              </label>
              <input type="text" class="form-control mt-0" id="vehicleNum" />
            </div>

            <div class="mb-3">
              <label for="make" class="form-label mb-0">
                Make
              </label>
              <input type="text" class="form-control mt-0" id="make" />
            </div>

            <div class="mb-3">
              <label for="vin" class="form-label mb-0">
                Vin/Chasis
              </label>
              <input type="text" class="form-control mt-0" id="vin" />
            </div>

            <div class="mb-3">
              <label for="vehicleType" class="form-label mb-0">
                Vehicle Type
              </label>
              <input type="text" class="form-control mt-0" id="vehicleType" />
            </div>
          </div>

          <div class="mb-3">
            <label for="buildDate" class="form-label mb-0">
              Build Date
            </label>
            <input type="text" class="form-control mt-0" id="buildDate" />
          </div>

          <div class="col-md-3 m-3">
            <h5 className="text-center">Registration</h5>
            <hr />
            <div class="mb-3">
              <label for="regNum" class="form-label mb-0">
                Registration#
              </label>
              <input type="text" class="form-control mt-0" id="regNum" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddVehicle;
