import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Table from "../layouts/Table";

const Vehicles = () => {
  const tableTitle = "Vehicles";
  const colNames = [
    "Name",
    "Registration",
    "VIN/Chassis#",
    "Make",
    "Model",
    "Build Date",
  ];
  const orderedColumns = [
    "name",
    "reg_Num",
    "vin",
    "make",
    "model",
    "build_Date",
  ];

  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    loadVehicles();
  }, []);

  const loadVehicles = async () => {
    const result = await axios.get("http://localhost:5000/vehicles");
    // console.log(result);
    result.data.map((item) => delete item.vehicle_id);
    setVehicles(result.data);
    // console.log(vehicles);
  };

  const deleteVehicle = async (id) => {
    await axios.delete(`http://localhost:5000/vehicles/${id}`);
    loadVehicles();
  };

  return (
    <div className="container">
      {/*       <div className="py-4">
        <h1>Vehicles</h1>
        <div className="table-title">
          <div className="row">
            <div className="col-sm-6">
              <h2>
                Manage <b>Vehicles</b>
              </h2>
            </div>
            <div className="col-sm-6">
              <Link className="btn btn-outline-light" to="vehicles/add">
                Add Vehicle
              </Link>
            </div>
          </div>
        </div> */}
      <Table
        tableTitle={tableTitle}
        colNames={colNames}
        tableData={vehicles}
        orderedColumns={orderedColumns}
      />
    </div>
  );
};

export default Vehicles;
