/*eslint-env es6*/
import React, { useState, useEffect } from "react";
import axios from "axios";

/* import Vehicles from "../pages/vehicles"; */

const Dropdown = (props) => {
  const [obj, setObj] = useState([]);

  useEffect(() => {
    loadObject();
  }, []);

  const loadObject = async () => {
    const result = await axios.get(
      `http://localhost:5000/${props.dropDownObject}`
    );
    //console.log(result.data);
    //  result.data.map((item) => delete item.id);
    setObj(result.data);
  };

  return (
    <>
      <select
        type="text"
        className="form-select mt-0"
        name="vehicleNum"
        // value={service.vehicleNum}
        onChange={props.onInputChange}
      >
        <option value="DEFAULT">Select</option>
        {obj.map((item, index) => (
          // <option value={item.id}>{item.regNum}</option>
          <option value={props.dropDownValue} dbId={item.id}>
            {item[props.dropDownKey]}
          </option>
        ))}
      </select>
    </>
  );
};
export default Dropdown;
