import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Table from "../layouts/Table";

const Vehicles = () => {
  const colNames = ["Name", "Registration", "VIN/Chassis#", "Make", "Model"];
  const [vehicles, setVehicles] = useState([]);

  useEffect(() => {
    loadVehicles();
  }, []);

  const loadVehicles = async () => {
    const result = await axios.get("http://localhost:5000/vehicles");
    // console.log(result);
    setVehicles(result.data.reverse());
  };

  const deleteVehicle = async (id) => {
    await axios.delete(`http://localhost:5000/vehicles/${id}`);
    loadVehicles();
  };

  return (
    <div className="container">
      <div className="py-4">
        <h1>Vehicles</h1>
        <div className="table-title">
          <div className="row">
            <div className="col-sm-6">
              <h2>
                Manage <b>Vehicles</b>
              </h2>
            </div>
            <div className="col-sm-6">
              <Link className="btn btn-outline-light" to="vehicles/add">
                Add Vehicle
              </Link>
            </div>
          </div>
        </div>
        <Table colNames={colNames} vehicles={vehicles} />
      </div>
    </div>
  );
};

export default Vehicles;
